mapper.select:
SELECT a.id, a.plate_number, in_time, out_time, combo_name, c.name as project_name FROM parking_record a LEFT JOIN purchase_record b ON ( a.plate_number = b.plate_number ) LEFT JOIN pay_combo p ON (b.combo_id = p.combo_id) left join project_info c on (a.project_id=c.id) where del_flag=0
and out_time is null and out_time is not null
and in_time >=#{start_date}
and out_time <=#{end_date}

SELECT id,project_id,plate_number,in_time,out_time,read_in_time,read_out_time FROM parking_record
where project_id=#{projectId}
ORDER BY read_out_time DESC LIMIT 0,1

SELECT id,project_id,plate_number,in_time,out_time,read_in_time,read_out_time FROM parking_record
where project_id=#{projectId}
ORDER BY read_in_time DESC LIMIT 0,1

mapper.update:
UPDATE parking_record SET

    in_time = #{inTime},


    read_out_time = #{readOutTime},


    out_time = #{outTime}

where id =#{id}
