mapper.select:
SELECT combo_name,effective_length,money
FROM pay_combo
WHERE combo_id=#{combo_id};

mapper.update:
UPDATE pay_combo p SET combo_name = #{combo_name},effective_length = #{effective_length},money = #{money}

    ,start_time =#{start_time}


    ,end_time =#{end_time}

WHERE (`combo_id`=#{combo_id});
